import React from "react";
import {useState, useEffect} from "react";
import Editor, { useMonaco } from "@monaco-editor/react";
import "./engine.module.css";

const AssessmentEngine = ({candidateName = '...'}) => {

const monaco = useMonaco();

var editor = monaco.editor.create(document.getElementById('container'), {
				value: ['function x() {', '\tconsole.log("Hello world!");', '}'].join('\n'),
				language: 'javascript'
			});

			/* eslint-disable */

// check for support
var pipEnabled = false;
var pipStatusMsg;
if (!('pictureInPictureEnabled' in document)) {
    pipStatusMsg = 'The Picture-in-Picture API is not available.';
} else if (!document.pictureInPictureEnabled) {
    pipStatusMsg = 'The Picture-in-Picture API is disabled.';
} else {
    pipEnabled = true;
}

var options = {
    controls: true,
    width: 320,
    height: 240,
    fluid: false,
    bigPlayButton: false,
    controlBar: {
        volumePanel: false
    },

    //60 min record time
    plugins: {
        record: {
            audio: false,
            video: true,
            pip: pipEnabled,
            displayMilliseconds: false,
            maxLength: 60*10*60*0.1,
            debug: true
        }
    }
};

// apply some workarounds for opera browser
applyVideoWorkaround();

var player = videojs('myVideo', options, function() {
    // print version information at startup
    var msg = 'Using video.js ' + videojs.VERSION +
        ' with videojs-record ' + videojs.getPluginVersion('record') +
        ' and recordrtc ' + RecordRTC.version;
    videojs.log(msg);
});

player.on('ready', function() {
    if (!pipEnabled) {
        alert(pipStatusMsg + ' Disabled the PiP button.');
    }
});

// error handling
player.on('deviceError', function() {
    console.warn('device error:', player.deviceErrorCode);
});

player.on('error', function(element, error) {
    console.error(error);
});

// user clicked the record button and started recording
player.on('startRecord', function() {
    console.log('started recording!');
});

// user completed recording and stream is available
player.on('finishRecord', function() {
    // the blob object contains the recorded data that
    // can be downloaded by the user, stored on server etc.
    console.log('finished recording: ', player.recordedData);
    /*var reader = new FileReader();
    reader.readAsDataURL(player.recordedData);
    reader.onloadend = function () {
      var base64String = reader.result;
      //console.log('Base64 String - ', base64String);
      //console.log('Base64 String without Tags- ',
      base64String.substr(base64String.indexOf(', ') + 1));
    }*/
});

// handle Picture-in-Picture events
var pipWindow;
player.on('enterPIP', function(element, evt) {
    console.log('Entered Picture-in-Picture');

    // listen for window resize
    pipWindow = evt.pictureInPictureWindow;
    pipWindow.addEventListener('resize', onPipWindowResize);

    console.log(`Window size is ${pipWindow.width} x ${pipWindow.height}`);
});
player.on('leavePIP', function() {
    console.log('Left Picture-in-Picture');

    // stop listening for resize
    pipWindow.removeEventListener('resize', onPipWindowResize);
});

function onPipWindowResize(evt) {
    // print window size to console
    console.log(`Window size changed to ${pipWindow.width} x ${pipWindow.height}`);
}

function showAlert() {
  //alert('Thanks for taking the sample live coding question. We will reach out to you shortly.');
  location.href='/myprofile';
}


return (
  <React.Fragment>

    <div>
      <div>
        <section class="section" style="padding-top: 24px;">
          <div class="container">

            <div>
              <div><b>Live coding question:</b>&nbsp;&nbsp;Write a function/method to check if a given string contains the word "garage". <br/><br/><span style="color: #05976e">*Use a programming language of your choice.</span>
                <span style="color:#666;font-size:0.8rem;">(Please allow <b>camera access</b>. Your video is kept private.)</span>
              </div>
              <br/>
              <div id="container" style="width: 100%;height: 500px;border: 1px solid grey;"></div>

              <video id="myVideo" playsinline class="video-js vjs-default-skin"></video>

              <div class="buttons mb-20" style="font-size: 2rem;display: block;margin: 0 auto;left: 0;right: 0;text-align: center;">

                <a class="button is-primary mr-4" style="margin-top:32px;" onclick="showAlert();">Next ></a></div>

            </div>


          </div>
        </section>
      </div>
      <div class="navbar-side is-hidden is-fixed is-top-0 is-left-0 has-background-blur" style="z-index: 50; width:100%; height: 100%;">
        <div class="is-absolute" style="z-index: -1;"></div>
        <div class="container is-relative">
          <a class="navbar-close is-absolute is-right-0 mt-6 mr-6" href="#">
            <img src="acros-assets/icons/mobile-decline.svg" alt=""/>
          </a>
        </div>
        <aside class="py-6 px-6 pb-8 has-background-white is-flex is-flex-direction-column is-justify-content-space-between" style="height: 100%; width: 288px; overflow-y: auto">
          <div>
            <a class="navbar-item title is-size-4 has-text-dark pl-4 mb-5" href="#">
              <img src="acros-assets/logo/logo.png" width="auto" style="max-height: initial;width:70px;" alt=""/>
            </a>
            <ul class="menu-list title is-size-6">
              <li><a class="py-4 px-6 has-text-grey-darker" href="#">Home</a></li>
              <li><a class="py-4 px-6 has-text-grey-darker" href="#cvLive">CV vs Live coding</a></li>
              <li><a class="py-4 px-6 has-text-grey-darker" href="#howItWorks">How it works</a></li>
              <li><a class="py-4 px-6 has-text-grey-darker" href="#practice">Take Practice Session</a></li>
            </ul>
          </div>
          <div>
            <div class="buttons mb-8"><a class="button is-dark is-outlined is-fullwidth mb-4" href="#">Log In</a><a class="button is-primary is-fullwidth" href="#">Sign up</a></div>
            <small class="has-text-grey-darker ml-6">2021 © Shuffle</small>
          </div>
        </aside>
      </div>
    </div>

    <section class="section py-16" id="cvLive" style="padding-top: 0px !important">
      <div class="container">
        <div class="columns is-align-items-center mb-0">
          <div class="column mb-16 mb-0-tablet" style="margin-top: 76px;">
            <h2 class="title mb-6 has-mw-lg" style="font-size: 2.2rem;"></h2>
            <p class="subtitle has-mw-md mb-12"><br/><br/><br/><br/><br/></p>

          </div>
          <div class="column">
          </div>
        </div>
      </div>
    </section>



    <hr class="gray-hr"/>
    <section class="section py-4"><div class="container">
      <div class="columns mb-0">
        <div>
          <a class="is-block mb-4 title is-size-4 has-text-dark" href="#">
            <p class="title is-size-6 has-text-grey-darker" style="opacity: 0.8;"> © 2022 Stint. All rights reserved.</p></a>
        </div>


      </div>
    </div>
    </section>
  </React.Fragment>
);
}
export default AssessmentEngine;
